mapper.insert:
/* DeliveryGatewayMapper.insertTraceCommonState */
INSERT INTO tb_trace_common_state (
    INVOICE_NO
    , PACK_END_DT
    , WH_CD
    , CST_CD
    , PARCEL_CD
    , LEVEL
    , DETAILS
    , REG_ID
    , MOD_ID
) VALUES (
    #{invoiceNo}
    , date_format(now(), '%Y%m%d')
    , #{whCd}
    , #{cstCd}
    , #{parcelCd}
    , #{level}
    , #{details}
    , #{regId}
    , #{modId}
)

mapper.update:
/* DeliveryGatewayMapper.updateTraceCommonState */
UPDATE tb_trace_common_state
SET LEVEL = #{level}
    , DETAILS = #{details}
    , MOD_TIME = now()
WHERE INVOICE_NO = #{invoice_no}

mapper.select:
/* DeliveryGatewayMapper.selectTraceCommonStateList */
SELECT
    INVOICE_NO
    , WH_CD
    , CST_CD
    , PARCEL_CD
    , LEVEL
    , DETAILS
FROM tb_trace_common_state
WHERE PACK_END_DT >= #{startDt}
  AND PACK_END_DT<=#{endDt}

  AND INVOICE_NO = #{invoiceNo}


  AND WH_CD = #{whCd}


  AND CST_CD = #{cstCd}


  AND PARCEL_CD = #{parcelCd}
